const rawContent = items[0].json.message.content;
let parsed;
try {
  parsed = JSON.parse(rawContent);
} catch (e) {
  throw new Error('Invalid JSON in content');
}

const keyMap = {
  'variantA': {
    subject: 'subject_A',
    emailBody: 'email_A',
    followUpBody: 'followUp_A',
    label: 'Variant A'
  },
  'variantB': {
    subject: 'subject_B',
    emailBody: 'email_B',
    followUpBody: 'followUp_B',
    label: 'Variant B'
  }
};

const parsedVariants = Object.entries(parsed).map(([key, value]) => {
  const map = keyMap[key] || {};
  return {
    variantName: map.label || key,
    subject: value[map.subject] || null,
    emailBody: value[map.emailBody] || null,
    followUpBody: value[map.followUpBody] || null
  };
});

return [{ json: { variants: parsedVariants } }];
